import "./App.scss";
import "react-s-alert/dist/s-alert-default.css";
import "react-s-alert/dist/s-alert-css-effects/slide.css";

import React, { Component } from "react";

import security from './security.png';
import banner from './banner.png';
import cta from './cta.png';
import message from './message.png';
import logo from './logo.png';
import mValue from './mValue.png';
import wValue from './wValue.png';
import { TxtType } from "./utils/utils";
import Alert from "react-s-alert";
import FontAwesomeIcon from "@fortawesome/react-fontawesome";
import ProfileCard from "./components/ProfileCard";
import _ from 'lodash';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isAuthorized: false,
      firstName: null,
      lastName: null,
      profileURL: null,
      pictureURL: null,
    };
  }

  componentDidMount() {
    window.addEventListener('message', this.handlePostMessage);
    var elements = document.getElementsByClassName("typewrite");
    for (var i = 0; i < elements.length; i++) {
      var toRotate = elements[i].getAttribute("data-type");
      var period = elements[i].getAttribute("data-period");
      if (toRotate) {
        new TxtType(elements[i], JSON.parse(toRotate), period);
      }
    }
  }

  handlePostMessage = (event) => {
    if (event.data.type === "profile") {
      this.updateProfile(event.data.profile);
      Alert.success(`Login successful: ${event.data.profile.localizedFirstName}`,{position:'top'});
    }
  };

  updateProfile = (profile) => {
    console.log(profile)
      this.setState({
        isAuthorized: true,
        firstName: _.get(profile,'localizedFirstName',''),
        lastName: _.get(profile,'localizedLastName',''),
        profileURL: `https://www.linkedin.com/in/${_.get(profile,'vanityName','')}`,
        pictureURL: _.get(_.last(_.get(profile,'profilePicture.displayImage~.elements','')),'identifiers[0].identifier','')
      })
  }

  requestProfile = () => {
    var oauthUrl = `https://www.linkedin.com/oauth/v2/authorization?response_type=code&client_id=${process.env.REACT_APP_CLIENT_ID}&scope=r_liteprofile&state=123456&redirect_uri=${process.env.REACT_APP_REDIRECT_URI}`
    var width = 450,
      height = 730,
      left = window.screen.width / 2 - width / 2,
      top = window.screen.height / 2 - height / 2;

    window.open(
      oauthUrl,
      "Linkedin",
      "menubar=no,location=no,resizable=no,scrollbars=no,status=no, width=" +
        width +
        ", height=" +
        height +
        ", top=" +
        top +
        ", left=" +
        left
    );
  };

  render() {
    return (
      <div className="App">
        <div style={{padding: '16px 0 16px 150px', boxSizing: 'border-box', background: 'black', display: 'flex', alignItems: 'center'}}>
          <img alt="logo" src={logo} width="200" />
          <div><span>534</span> <span style={{color: 'white', opacity: '0.8' }}>Linkedin Professionals have already signed up with us</span> <span>Try NOW</span></div>
        </div>
        <div className="banner-section">
          <div className="banner">
            <img alt="banner" src={banner} width="440" height="450" />            
          </div>
          <div className="banner-content">
            <div className="header">Profession oriented match-making</div>
            <div style={{height: '100px'}}>
              <div className="sub-header">Find a partner with similar</div>
              <div
                className="typewrite"
                data-period="2000"
                data-type='[ "interests", "goals", "career" ]'
              ></div>
            </div>
            <div className="cta">
              <img alt="cta" src={cta} width="350" />
            </div>
          </div>
        </div>
        <div className="value-props">
          <div className="woman">
            <img alt="woman" src={wValue} width="450" style={{borderRadius: '10px'}} />
          </div>
          <div className="man">
            <img alt="man" src={mValue} width="450" style={{borderRadius: '10px'}} />
          </div>
        </div>
        <div className="security" style={{textAlign: 'center', marginTop: '150px'}}>
          <img src={security} alt="security" width="1000" />
        </div>
        <div className="messaging" style={{textAlign: 'center', marginTop: '150px', paddingBottom: '80px'}}>
          <img src={message} alt="message" width="1000" />
        </div>
      </div>
    );
  }
}

export default App;
